
public class StringCharAt {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// index    0123456789  
		String s = "hello word";
		char cAt= s.charAt(4);
		char cat2v= s.charAt(5);
		System.out.println(cAt);
		System.out.println(cat2v);
		System.out.println(s.length());
	}

}
